// HOME TIMELINE COMPONENT
// ═══════════════════════════════════════════════════════════════════════

function HomeTimeline({ theme, onEraSelect }) {
  const [currentIndex, setCurrentIndex] = useState(0);
  const [isAnimating, setIsAnimating] = useState(false);
  
  const c = theme === 'dark' ? {
    bg: '#080706',
    text: '#e5e5e5',
    muted: '#737373',
    border: '#1f1d1b'
  } : {
    bg: '#fafafa',
    text: '#171717',
    muted: '#737373',
    border: '#e5e5e5'
  };

  const goTo = (index) => {
    if (isAnimating) return;
    const newIndex = Math.max(0, Math.min(TIMELINE_ERAS.length - 1, index));
    if (newIndex !== currentIndex) {
      setIsAnimating(true);
      setCurrentIndex(newIndex);
      setTimeout(() => setIsAnimating(false), 350);
    }
  };

  const goPrev = () => goTo(currentIndex - 1);
  const goNext = () => goTo(currentIndex + 1);

  // Keyboard navigation
  useEffect(() => {
    const handleKey = (e) => {
      if (e.key === 'ArrowLeft') goPrev();
      if (e.key === 'ArrowRight') goNext();
    };
    window.addEventListener('keydown', handleKey);
    return () => window.removeEventListener('keydown', handleKey);
  }, [currentIndex, isAnimating]);

  const era = TIMELINE_ERAS[currentIndex];
  const progress = currentIndex / (TIMELINE_ERAS.length - 1);

  return (
    <div style={{ borderTop: `1px solid ${c.border}` }}>
      {/* Header */}
      <div style={{ padding: 'var(--space-section) var(--space-page)' }}>
        <div style={{ 
          display: 'grid', 
          gridTemplateColumns: 'var(--label-col) 1fr auto', 
          gap: 'var(--gap-md)',
          alignItems: 'center'
        }}>
          <span style={{ 
            fontSize: 'var(--type-small)', 
            color: c.muted,
            lineHeight: 'var(--row-height)',
            height: 'var(--row-height)',
            display: 'flex',
            alignItems: 'center'
          }}>Timeline</span>
          <span style={{ 
            fontSize: 'var(--type-small)', 
            color: c.muted,
            lineHeight: 'var(--row-height)',
            height: 'var(--row-height)',
            display: 'flex',
            alignItems: 'center'
          }}>
            The Six Eras ⌐
          </span>
          <div style={{ display: 'flex', gap: '12px', alignItems: 'center' }}>
            {/* Prev arrow - metro style */}
            <button
              onClick={goPrev}
              disabled={currentIndex === 0}
              style={{
                background: 'none',
                border: 'none',
                padding: 0,
                cursor: currentIndex === 0 ? 'default' : 'pointer',
                opacity: currentIndex === 0 ? 0.3 : 1,
                transition: 'opacity 0.2s ease'
              }}
            >
              <span style={{
                display: 'inline-flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '28px',
                height: '28px',
                border: `1.5px solid ${c.muted}`,
                borderRadius: '50%',
                transition: 'all 0.2s ease'
              }}>
                <svg 
                  width="12" 
                  height="12" 
                  viewBox="0 0 24 24" 
                  fill="none" 
                  stroke={c.muted}
                  strokeWidth="2.5"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <path d="M19 12H5M12 19l-7-7 7-7"/>
                </svg>
              </span>
            </button>
            
            <span style={{ 
              fontSize: 'var(--type-caption)', 
              color: c.muted, 
              minWidth: '40px', 
              textAlign: 'center',
              lineHeight: 'var(--row-height)'
            }}>
              {currentIndex + 1} / {TIMELINE_ERAS.length}
            </span>
            
            {/* Next arrow - metro style */}
            <button
              onClick={goNext}
              disabled={currentIndex === TIMELINE_ERAS.length - 1}
              style={{
                background: 'none',
                border: 'none',
                padding: 0,
                cursor: currentIndex === TIMELINE_ERAS.length - 1 ? 'default' : 'pointer',
                opacity: currentIndex === TIMELINE_ERAS.length - 1 ? 0.3 : 1,
                transition: 'opacity 0.2s ease'
              }}
            >
              <span style={{
                display: 'inline-flex',
                alignItems: 'center',
                justifyContent: 'center',
                width: '28px',
                height: '28px',
                border: `1.5px solid ${c.muted}`,
                borderRadius: '50%',
                transition: 'all 0.2s ease'
              }}>
                <svg 
                  width="12" 
                  height="12" 
                  viewBox="0 0 24 24" 
                  fill="none" 
                  stroke={c.muted}
                  strokeWidth="2.5"
                  strokeLinecap="round"
                  strokeLinejoin="round"
                >
                  <path d="M5 12h14M12 5l7 7-7 7"/>
                </svg>
              </span>
            </button>
          </div>
        </div>
      </div>

      {/* Progress bar with era markers */}
      <div style={{ 
        height: '1px', 
        background: c.border,
        margin: '0 var(--space-page) 0 var(--space-page)',
        position: 'relative'
      }}>
        <div style={{ 
          position: 'absolute',
          left: 0,
          top: 0,
          height: '1px',
          width: `${progress * 100}%`,
          background: c.muted,
          transition: 'width 0.4s cubic-bezier(0.16, 1, 0.3, 1)'
        }} />
        {/* Era dots */}
        {TIMELINE_ERAS.map((_, idx) => (
          <button
            key={idx}
            onClick={() => goTo(idx)}
            style={{
              position: 'absolute',
              left: `${(idx / (TIMELINE_ERAS.length - 1)) * 100}%`,
              top: '50%',
              transform: 'translate(-50%, -50%)',
              width: idx === currentIndex ? '10px' : '6px',
              height: idx === currentIndex ? '10px' : '6px',
              borderRadius: '50%',
              background: idx <= currentIndex ? c.muted : c.border,
              border: 'none',
              cursor: 'pointer',
              transition: 'all 0.3s ease',
              padding: 0
            }}
          />
        ))}
      </div>

      {/* Era content */}
      <div style={{ 
        padding: 'var(--space-page)',
        minHeight: '320px',
        position: 'relative',
        overflow: 'hidden'
      }}>
        <div 
          key={currentIndex}
          style={{
            display: 'grid',
            gridTemplateColumns: 'var(--label-col) 1fr 1fr',
            gap: 'var(--gap-lg)',
            animation: 'fadeSlideIn 0.4s cubic-bezier(0.16, 1, 0.3, 1)'
          }}
        >
          {/* Era number column */}
          <div>
            <div style={{ 
              fontSize: 'var(--type-caption)', 
              color: c.muted,
              letterSpacing: '0.1em',
              marginBottom: '8px',
              lineHeight: 1
            }}>
              ERA
            </div>
            <div style={{ 
              fontSize: 'clamp(48px, 6vw, 72px)', 
              fontWeight: 200, 
              color: c.text,
              lineHeight: 1,
              opacity: 0.3
            }}>
              {era.era}
            </div>
          </div>

          {/* Main content column */}
          <div>
            <div style={{ 
              fontSize: 'var(--type-h2)', 
              fontWeight: 400, 
              color: c.text,
              marginBottom: '8px',
              lineHeight: 1.2
            }}>
              {era.title}
            </div>
            <div style={{ 
              fontSize: 'var(--type-nav)', 
              color: c.muted,
              fontStyle: 'italic',
              marginBottom: 'var(--space-section)'
            }}>
              {era.subtitle}
            </div>
            <div style={{
              fontSize: 'var(--type-caption)',
              color: c.muted,
              marginBottom: 'var(--space-section)',
              letterSpacing: '0.05em',
              textTransform: 'uppercase'
            }}>
              {era.duration}
            </div>
            <p style={{ 
              fontSize: 'var(--type-body)', 
              color: c.text, 
              lineHeight: 1.7,
              maxWidth: '400px'
            }}>
              {era.description}
            </p>
            
            {/* Link to full entry */}
            <button
              onClick={() => onEraSelect && onEraSelect('cosmology', `era-${currentIndex}`)}
              style={{
                marginTop: 'var(--space-section)',
                background: 'none',
                border: 'none',
                color: c.muted,
                fontSize: 'var(--type-caption)',
                cursor: 'pointer',
                padding: 0,
                textDecoration: 'underline',
                textUnderlineOffset: '3px'
              }}
            >
              Read full entry →
            </button>
          </div>

          {/* Events column */}
          <div style={{ borderLeft: `1px solid ${c.border}`, paddingLeft: 'var(--gap-md)' }}>
            <div style={{ 
              fontSize: 'var(--type-caption)', 
              color: c.muted,
              letterSpacing: '0.1em',
              marginBottom: 'var(--space-element)',
              textTransform: 'uppercase',
              lineHeight: 1
            }}>
              Key Events
            </div>
            {era.events.map((event, evtIdx) => (
              <div 
                key={evtIdx}
                style={{
                  display: 'flex',
                  alignItems: 'flex-start',
                  gap: '12px',
                  marginBottom: '12px',
                  opacity: 0,
                  animation: `fadeSlideIn 0.4s cubic-bezier(0.16, 1, 0.3, 1) ${0.1 + evtIdx * 0.08}s forwards`
                }}
              >
                <span style={{ 
                  marginTop: '7px',
                  width: '4px', 
                  height: '4px', 
                  borderRadius: '50%',
                  background: c.muted,
                  flexShrink: 0
                }} />
                <span style={{ 
                  fontSize: 'var(--type-nav)', 
                  color: c.muted,
                  lineHeight: 1.5
                }}>
                  {event}
                </span>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* CSS animation */}
      <style>{`
        @keyframes fadeSlideIn {
          from {
            opacity: 0;
            transform: translateX(20px);
          }
          to {
            opacity: 1;
            transform: translateX(0);
          }
        }
      `}</style>
    </div>
  );
}

